    {
        doNotRunUntil = "2022-05-10T14:57:31.2311892-04:00",
        doNotRunAfter = "2022-05-10T14:57:31.2311892-04:00",
        startWindow = "PT1H23M45S",
        recurrenceInterval = "PT1H23M45S",
    },
    jobSpecification = new
