Schema for OperationStatus: { id: string, # Optional. Fully qualified ID for the operation status. name: string, # Optional. The operation id name status: string, # Required. Provisioning state of the resource. resourceId: string, # Optional. The id of the resource. startTime: string (ISO 8601 Format), # Optional. The start time of the operation endTime: string (ISO 8601 Format), # Optional. The end time of the operation percentComplete: number, # Optional. Percent of the operation that is complete properties: AnyObject, # Optional. Custom operation properties, populated only for a successful operation. error: { code: string, # Optional. The error code. message: string, # Optional. The error message. }, # Optional. Operation Error message }

